mapper.select:
SELECT it_news_title AS title, it_news_link AS link, it_news_contents AS content, mailing_date AS mailingDate FROM it_news
AND it_news_id = #{itNewsId}
AND it_news_title LIKE CONCAT('%', #{title}, '%')
AND DATE_FORMAT(mailing_date, '%Y-%m-%d') = #{mailingDate}
ORDER BY mailing_date DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM it_news


        AND it_news_id = #{itNewsId}


        AND it_news_title LIKE CONCAT('%', #{title}, '%')


        AND DATE_FORMAT(mailing_date, '%Y-%m-%d') = #{mailingDate}
