mapper.select:
SELECT interview_room_id, user_id, interview_room_title, interview_category, difficulty_level, evaluation_strictness, interview_room_status, interview_room_created_at FROM INTERVIEW_ROOM WHERE user_id = #{userId} AND interview_room_status != 'EXPIRED'
AND interview_category = #{category}
AND difficulty_level = #{difficultyLevel}
AND evaluation_strictness = #{evaluationStrictness}
ORDER BY interview_room_id DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM INTERVIEW_ROOM
WHERE user_id = #{userId}
    AND interview_room_status != 'EXPIRED'

        AND interview_category = #{category}


        AND difficulty_level = #{difficultyLevel}


        AND evaluation_strictness = #{evaluationStrictness}

SELECT interview_room_id, user_id, interview_room_title, interview_category, difficulty_level, evaluation_strictness, interview_room_status, interview_room_created_at FROM INTERVIEW_ROOM WHERE 1=1
AND interview_room_status = #{status}
AND interview_category = #{category}
AND difficulty_level = #{difficultyLevel}
AND evaluation_strictness = #{evaluationStrictness}
ORDER BY interview_room_id DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM INTERVIEW_ROOM
WHERE 1=1

    AND interview_room_status = #{status}


    AND interview_category = #{category}


    AND difficulty_level = #{difficultyLevel}


    AND evaluation_strictness = #{evaluationStrictness}
